mapper.insert:
INSERT INTO PRODUCT
		(
			PD_NO
			,PD_NAME
			,PD_GENDER
			,PD_KIND
			,PD_PRICE
			,PD_SALE
			,PD_VACCINE
			,PD_BIRTH
			,PD_IMG
			,PD_REGDATE
		
		)
	VALUES
		(
		PRODUCTSEQ.NEXTVAL
		,#{pdName}
		,#{pdGender}
		,#{pdKind}
		,#{pdPrice}
		,#{pdSale}
		,#{pdVaccine}
		,#{pdBirth}
		,#{pdImg}
		,SYSDATE
		)

mapper.update:
UPDATE 
	PRODUCT SET 
			PD_NAME=#{pdName}
			,PD_GENDER=#{pdGender}
			,PD_KIND=#{pdKind}
			,PD_PRICE=#{pdPrice}
			,PD_SALE=#{pdSale}
			,PD_VACCINE=#{pdVaccine}
			,PD_BIRTH=#{pdBirth}
			,PD_IMG=#{pdImg}
	WHERE PD_NO=#{pdNo}
